export const DECREASE='DECREASE';

export function increaseActionFunc(){
    return {
        type:INCREASE,
        preload:'increase the current value'
    }
}

export function decreaseActionFunc(){
    return {
        type:DECREASE,
        preload:'decrease the current value'
    }
}
