import React, { Component } from 'react';

class Product extends Component {
    state = { 
        data: []
     }

    getAjaxInfo = () => {
        // let url = 'https://www.easy-mock.com/mock/5b8944fda3c04e6853e33f3e/react/ad/adcount';
        let url = 'https://api.github.com/users/chriscoyier/repos';
        fetch(url)
            // .then(res => {console.log(res)}) 
            .then(response => response.json())
            .then(data => this.setState({data: data}))
            // .catch(error => console.log('error is', error));

    }

    render() { 
        return ( 
            <div className='product'>Product
                <div style={{width: '100%', height: '200px', borderTop : '1px solid #e1e1e1', borderBottom: '1px solid #e1e1e1'}}>
                    <button onClick={this.getAjaxInfo}>getAjaxInfo</button>
                    <div>

                    </div>
                </div>

            </div>

         );
    }
}

export default Product;
