import React, { Component } from 'react';

class Product extends Component {
    state = {  }

    getAjaxInfo = () => {
        fetch('https://api.github.com/users/chriscoyier/repos')
            .then((response) => {
                console.log(response)
            })

    }

    render() { 
        return ( 
            <div className='product'>Product
                <div style={{width: '100%', height: '200px', borderTop : '1px solid #e1e1e1', borderBottom: '1px solid #e1e1e1', textAlign: 'center'}}>
                    <button onClick={this.getAjaxInfo}>getAjaxInfo</button>
                </div>

            </div>

         );
    }
}

export default Product;
