import React, { Component } from 'react';
import { 
    BrowserRouter as Router, 
    Route, 
    NavLink,
    Switch,
} from 'react-router-dom';
import Home from './child_components/Home';
import Category from './child_components/Category';
import Product from './child_components/Product';
import Contact from './child_components/Contact';
import './Main.less';
import { connect } from 'react-redux';
import { increaseActionFunc, decreaseActionFunc } from './redux/action/Action';

class Main extends Component {
    constructor(props) {
        super(props)

        this.state = { 
            menuIds: [{
                menuId: 1,
                path: '/',
                menuName: '首页',
                component: 'Home'
            },{
                menuId: 2,
                path: '/category',
                menuName: '分类',
                component: 'Category'
            },{
                menuId: 3,
                path: '/product',
                menuName: '产品',
                component: 'Product'
            },{
                menuId: 4,
                path: '/contact',
                menuName: '联系我们',
                component: 'Contact'
            }]
         }
    }

    componentDidMount = (value) => {
        console.log(value)
        console.log(this.props);
    }

    render() { 
        let { menuIds } = this.state;
        const {value, increase, decrease} = this.props;
        return ( 
            <Router>
                <div className="main">
                    <nav>
                        {/* <ul> */}
                            {
                                menuIds.map((item, index) => 
                                    <li key={item.menuId}><NavLink to={item.path}>{item.menuName}</NavLink></li>
                                )
                            }
                        {/* </ul> */}
                    </nav>
                    <div className="wrapper">
                        {/* <Switch>
                            <Route exact path='/' component={ Home }/>
                            <Route path='/category' component={ Category }/>
                            <Route path='/product' component={ Product }/>
                            <Route path='/contact' component={ Contact }/>
                            <Route path='*' component={ Home }/>
                        </Switch> */}
                        <button onClick={increase}>+</button>
                        <span>value:{value}</span>
                        <button onClick={decrease}>-</button>
                    </div>
                </div>
            </Router>
         );
    }

}
const mapStateToProps = (state) => {
    return {
        value: state.value
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        increase: () => {
            dispatch(increaseActionFunc())
        },
        decrease: () => {
            dispatch(decreaseActionFunc())
        }
    }
}

Main = connect()(Main);

export default Main;
