import React, { Component } from 'react';
import EasyDragSort from '../tools/EasyDragSort';
import './style/Category.less';

class Category extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: [{id: 'title'},{id: 'name'},{id: 'code'},{id: 'email'},{id: 'a'},{id: 'b'}],
            curMoveItem: null
        }
    }

    handleDragMove = (data, from, to) => {
        this.setState({
          curMoveItem: to,
          list: data
        })
      }

      handleDragEnd = () =>{
        this.setState({
          curMoveItem: null
        })
      }

    state = {  }
    render() { 
        return ( 
            <div className="main">
                <h3>react-drag-sort</h3>
                <EasyDragSort onDragEnd={this.handleDragEnd} onChange={this.handleDragMove} data={this.state.list} >

                    {this.state.list.map( (item, index) =>{
                    return <div  className={this.state.curMoveItem === index? 'item active' : 'item' }
                        key={item.id}
                        // onClick={()=> {
                        // let newItems = this.state.list.slice();
                        // newItems.splice(index, 1);
                        // this.setState({list: newItems});
                        // }}
                        style={{width: '100px', height: '100px', float: 'left', border: '1px solid red', marginLeft: '50px', textAlign: 'center', lineHeight: '100px'}}
                    >{item.id}</div>
                    })}
                </EasyDragSort>
            </div>
         );
    }
}

export default Category;
