import React, { Component } from 'react';
// import Button from 'antd/lib/button';
import './style/Home.less';
import 'antd/lib/button/style/css';
import { Button } from 'antd';
import createStore from 'antd/lib/table/createStore';
import ReactDOM from 'react-dom';

const reducer = (state = 0, action ) => {
    switch (action.type) {
        case 'ADD': return state + 1;
        case 'DECREASE': return state - 1;
        default: return state;
    }
}
let store = createStore(reducer)
class Home extends Component {
    state = {  }
    render() { 
        return ( 
            <div className="home">
                value:<p>{this.props.state}</p>
                <Button onClick={this.props.onADD}>按钮 +</Button>
                <Button onClick={this.props.onDECREASE}>按钮 -</Button>
            </div>
         );
    }
}
const render = () => {
    ReactDOM.render(
        <Home
            value={store.getState()}
            onADD={ () => store.dispatch({type: 'ADD'})}
            onDECREASE={ () => store.dispatch({type: 'DECREASE'})}
        />
    )
}
store.subscribe(render)
export default Home;
